<?phpclass Gallery extends CI_Controller {		public function index(){		$data['main_content'] = 'gallery/gallery_view';		$this->load->view('template/main', $data);	}		public function add(){				//$data['main_content'] = 'gallery/gallery_view';		//$this->load->view('template/main');				$file = $_FILES['resume'];		if($this->doUpload($file)){			$this->session->set_flashdata('success' , 'Uploaded successfully');			redirect('gallery/index');		}	}		public function doUpload($files){		$count = count($files['name']);		if($count > 0){			for($i = 0; $i < $count; $i++){				$file = $files['name'][$i];				$tmpPath = $files['tmp_name'][$i];				move_uploaded_file($tmpPath, 'uploads/'.$file);			}			return true;		}		return false;	}}